import { useState, useEffect } from 'react';
import {  MyAPI_URL } from './myapi';


function Test() {
    
    //const [favoriteweather, setFavoriteweather] = useState({});
    let favoriteweather={};
    /*
    useEffect(() => {
        console.log(favoriteweather);
      }, []);
    */
    const weather=(adress)=>{
        console.log('FetchFavoriteweathers lancée1');
        fetch("http://localhost:5000/locations/weather/"+adress)
          .then((response) => response.json())
          .then((data) => {
            if ( data) {
              console.log('FetchFavoriteweathers lancée2')
              favoriteweather=data.main
              console.log(data);
              return null;
            } else {
                console.log('FetchFavoriteweathers lancée0')
              return;
          }})
          .catch((error) => {
            console.error('Failed to fetch favorites:', error);
            return;
          });
        }

        weather('Casablanca');
        console.log(favoriteweather);

      
}
export default Test;
