    useEffect(() => {
      const weather = async () => {
        console.log('FetchFavoriteweathers lancée1');
        try {
          const response = await fetch("http://localhost:5000/locations/weather/Casablanca");
          const data = await response.json();
          if (data) {
            console.log('FetchFavoriteweathers lancée2')
            setFavoriteweather(data);
          } else {
            console.log('FetchFavoriteweathers lancée0')
          }
        } catch (error) {
          console.error('Failed to fetch favorites:', error);
        }
      };
  
      weather();
    }, []);
  
    console.log(11);
    console.log(favoriteweather);
  */
  
    

    const [favoriteLocations, setFavoriteLocations] = useState(['Casablanca', 'Rabat']);
    const [favoriteWeather, setFavoriteWeather] = useState([]);

  useEffect(() => {
    fetchFavoriteWeather();
  }, [favoriteLocations]);

  const fetchFavoriteWeather = () => { 
    const fetchWeather = async (location) => {
      try {
        const response = await fetch(`http://localhost:5000/locations/weather/${location}`);
        const data = await response.json();
        if (data) {
          return data;
        }
        return null;
      } catch (error) {
        console.error('Failed to fetch weather:', error);
        return null;
      }
    };

    const fetchAllWeather = async () => {
      const weatherPromises = favoriteLocations.map((location) => fetchWeather(location));
      const weatherData = await Promise.all(weatherPromises);
      setFavoriteWeather(weatherData.filter((data) => data !== null));
      console.log(favoriteWeather)
    };

    fetchAllWeather();
    
  };

  return (
    <>
    {
        favoriteLocations.map((favoriteLocation) => (
        <div  className="favorite-item">
          <CurrentWeather data={favoriteWeather[favoriteLocations.indexOf(favoriteLocation)]} />
        </div>
      ))}
    </>
  ) 
}
  
export default Test;
